    private void IDtxtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_IDtxtActionPerformed
        // TODO add your handling code here:
    }//GEN-LAST:event_IDtxtActionPerformed
     EmployeeDao edao=new EmployeeDao();
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       if("".equals(IDtxt.getText())||"".equals(nametxt.getText())||"".equals(jobtxt.getText())||"".equals(dobtxt.getText())||""
               .equals(typetxt.getSelectedItem().toString())||"".equals(salarytxt.getText())){
           
        statustxt.setText("Any of attributes cannot be empty");
           
       }else{
               try{
                Employee eobj=new Employee(Integer.parseInt(IDtxt.getText().trim()),nametxt.getText().trim(),jobtxt.getText().trim(), 
                        dobtxt.getText(), typetxt.getSelectedItem().toString(),Integer.parseInt(salarytxt.getText()));
              
                edao.insert(eobj);
             
                statustxt.setText("Inserted Successfully" );
                IDtxt.setText("");
                nametxt.setText("");
                jobtxt.setText("");
                dobtxt.setText("");
                salarytxt.setText("");
               
                }catch(Exception ex){
                    statustxt.setText("Error" );
                }
               
               
            }
    }//GEN-LAST:event_jButton1ActionPerformed

