    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed

        if("".equals(IDtxt.getText())){
           statustxt.setText("Employee ID cannot be empty");
           
       }
       else{
           Employee eobj=edao.searchbyid(Integer.parseInt(IDtxt.getText()));
           if(String.valueOf(eobj.getID())==null){
               statustxt.setText("No Employee found with the entered ID");
           
           }
           else{
                IDtxt.setText(String.valueOf(eobj.getID()));
                nametxt.setText(eobj.getname());
                jobtxt.setText(eobj.getJobT());
                dobtxt.setText(eobj.getDOB());
                typetxt.setSelectedItem(eobj.gettype());
                salarytxt.setText(String.valueOf(eobj.getsalary()));
                statustxt.setText("");
            }
       }
               
       
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        if("".equals(IDtxt.getText())||"".equals(nametxt.getText())||"".equals(jobtxt.getText())||"".equals(dobtxt.getText())||"".equals(typetxt.getSelectedItem())||"".equals(salarytxt.getText())){
        statustxt.setText("Value should be given to all the Attributes");
           
       }else{
               Employee eobj=new Employee(Integer.parseInt(IDtxt.getText().trim()),nametxt.getText().trim(),jobtxt.getText().trim(), dobtxt.getText(),typetxt.getSelectedItem().toString(),Integer.parseInt(salarytxt.getText()) );
               boolean status=edao.update(eobj);
               if(status==true){
               statustxt.setText("Employee Updated Successsfully");
               IDtxt.setText("");
               nametxt.setText("");
               jobtxt.setText("");
               dobtxt.setText("");
               typetxt.setSelectedItem("");
               salarytxt.setText("");
               } 
               else{
               statustxt.setText("Update Employee Failed");
               
               
               }
               
               }
    
    
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
       if ("".equals(IDtxt.getText())) {
            statustxt.setText("Employee ID cannot be empty");
        } else {

            boolean status = edao.deleteById(Integer.parseInt(IDtxt.getText().trim()));
            if (status == true) {
                statustxt.setText("Employee deleted successfully");
            } else {
                statustxt.setText("Delete Enployee failed");
            }
        }
    }//GEN-LAST:event_jButton3ActionPerformed

